        $(".ir-arriba").click(function() {
            $("body, html").animate({
                scrollTop: 0
            }, 1e3)
        }),
        $("span[class^='e64']").attr("class", function() {
            $(this).html(base64_decode($(this).html()))
        }),
        $(function() {
            $("#birthday_day, #birthday_month").keyup(function() {
                this.value = this.value.replace(/[^0-9]/g, "")
            })
        }),
        $("#customCheck8").click(function() {
            $("#customCheck8").is(":checked") ? $("#MMERGE5").show() : $("#MMERGE5").hide()
        }),
        $(".btn-suscribirme").click(function(e) {
            e.preventDefault();
            var a = $(this).parents("form");
            $(function() {
                $.ajax({
                    url: "https://www.megatravel.com.mx/newslet/po",
                    data: a.serialize(),
                    type: "POST",
                    dataType: "html",
                    success: function(e) {
                        var a = jQuery.parseJSON(e);
                        if (a.errors) {
                            var i = "";
                            a.errors.FNAME && (i += "<li>NOMBRE: </li>"),
                                a.errors.LNAME && (i += "<li>APELLIDOS: </li>"),
                                a.errors.EMAIL && (i += "<li>EMAIL: </li>"),
                                a.errors.birthday_day && (i += "<li>DÍA CUMPLEAÑOS: </li>"),
                                a.errors.birthday_month && (i += "<li>MES CUMPLEAÑOS: </li>"),
                                a.errors.MMERGE5 && (i += "<li>AGENCIA: </li>"),
                                a.errors.ESTADO && (i += "<li>ESTADO: </li>"),
                                "" != i && ($("#resp_news").show(),
                                    $("#resp_news ul").html(i))
                        }
                        a.success && ($("#resp_news").show(),
                            $("#resp_news").removeClass("alert-danger").addClass("alert-success"),
                            $("#resp_news ul").html(a.success))
                    }
                })
            })
        })
